INSERT INTO `wp_postmeta` VALUES (2944,167,'c8_templates_2_content','<h3><span style=\"font-family: \'times new roman\', times, serif;\">Connecting Your Business to What Matters Most</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">At Encompass, we are committed to improving the way the beverage industry works. <br />\r\n</span><span style=\"font-family: \'times new roman\', times, serif;\">How people make decisions, how they serve customers, and how they run their businesseswe bring it all together. <br />\r\n</span><span style=\"font-family: \'times new roman\', times, serif;\">Our cloud-native, secure, and compliant solutions are optimized for continuous innovation. Our ERP solutions unlock business growth opportunities by enabling each tier of the supply chain to drive operational efficiencies, improve how people work, and meet fast-changing, unpredictable consumer demand.</span></p>');
