INSERT INTO `wp_postmeta` VALUES (2947,167,'_c8_templates_3_contentleft','field_63a98b16e138b');
INSERT INTO `wp_postmeta` VALUES (2948,167,'c8_templates_3_contentright_0_col','<h3>Drive Business Growth</h3>\r\n<p>Tap into the power of predictive analytics and demand forecasting. Capitalize on revenue-generating opportunities faster. Capture mission-critical data across your entire operationincluding all three supply chain tiersempowering you to bring your beverage or food business into new levels of profitability.</p>');
INSERT INTO `wp_postmeta` VALUES (2949,167,'_c8_templates_3_contentright_0_col','field_63a98b41e138d');
INSERT INTO `wp_postmeta` VALUES (2950,167,'c8_templates_3_contentright_1_col','<h3>Leverage Real-Time Data for Better Decision-Making</h3>\r\n<p>Accelerate business decisions with better, more accurate system-to-system data and comprehensive reporting that results in deep, actionable insights. Stay ahead of potential disruptions and maintain a finger on the pulse of your supply chain from anywhere in the world</p>');
INSERT INTO `wp_postmeta` VALUES (2951,167,'_c8_templates_3_contentright_1_col','field_63a98b41e138d');
INSERT INTO `wp_postmeta` VALUES (2952,167,'c8_templates_3_contentright_2_col','<h3>Improve Operational Efficiencies</h3>\r\n<p>Streamline processes and save money by reducing leakage with a fully integrated ERP system that works for your business. Encompass ERP makes it possible to identify where, when, and how resources can be best allocated to provide the most optimal outcome.</p>\r\n<div class=\"ddict_btn\" style=\"top: 16px; left: 243.675px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>');
INSERT INTO `wp_postmeta` VALUES (2953,167,'_c8_templates_3_contentright_2_col','field_63a98b41e138d');
INSERT INTO `wp_postmeta` VALUES (2954,167,'c8_templates_3_contentright_3_col','<h3>Enhance Team Dynamics &amp; Employee Satisfaction</h3>\r\n<p>When your labor force has the tools they need to succeed, the workplace runs like a well-oiled machine. With Encompass ERP, every stakeholder across your supply chainfrom purchasing to Point-of-Salehas what they need to do their jobs effectively.</p>\r\n<div class=\"ddict_btn\" style=\"top: 12px; left: 365.75px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>');
