INSERT INTO `wp_postmeta` VALUES (4556,174,'c8_templates_5_content','<h3><span style=\"font-size: 24pt;\">Our Open Ecosystem of Partners Gives You the Power to Choose</span></h3>\r\n<p><span style=\"font-size: 18pt;\">We believe in the power of transparency and connectedness. Encompass ecosystem of partners combines diversity and best-in-class API functionality to provide one powerful, unified source of truth for our clients, no matter how many modules they choose to utilize.</span></p>\r\n<p><span style=\"font-size: 18pt;\">With Encompass ERP, you choose only the tools and partners you need, leaving out what isnt necessary. Finally, your beverage or food supply chain can now be managed with a single sign-on experience without compromising efficiency or breaking data flow.</span></p>\r\n<div class=\"ddict_btn\" style=\"top: 25px; left: 887.388px;\"></div>\r\n<div class=\"ddict_btn\" style=\"top: 129.4px; left: 887.388px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>');
